package net.jeeshop.web.action.manage.keyvalue;import net.jeeshop.core.BaseAction;import net.jeeshop.services.manage.keyvalue.KeyvalueService;import com.tuisitang.modules.shop.entity.Keyvalue;public class KeyvalueAction extends BaseAction<Keyvalue> {	private static final long serialVersionUID = 1L;	private KeyvalueService keyvalueService;	public KeyvalueService getKeyvalueService() {		return keyvalueService;	}	protected void selectListAfter() {		pager.setPagerUrl("keyvalue!selectList.action");	}	public void setKeyvalueService(KeyvalueService keyvalueService) {		this.keyvalueService = keyvalueService;	}	public Keyvalue getE() {		return this.e;	}	public void prepare() throws Exception {		if (this.e == null) {			this.e = new Keyvalue();		}		super.initPageSelect();	}	public void insertAfter(Keyvalue e) {		e.clear();	}	@Override	public String insert() throws Exception {		super.insert();		return super.selectAllList;	}	@Override	public String update() throws Exception {		super.update();		return selectList();	}	}