mapper.update:
update t_orderdetail
set id= #{id}
,orderID = #{orderID}
,productID = #{productID}
,price = #{price}
,number = #{number}
,productName = #{productName}
,picture = #{picture}
,fee = #{fee}
,total0 = #{total0}
,isComment = #{isComment}
,lowStocks = #{lowStocks}
,score = #{score}
,specId = #{specId}
,specInfo = #{specInfo}
,sellerId = #{sellerId}
,sellerName = #{sellerName}
,giftID = #{giftID}
where id = #{id}

mapper.select:
select t.* from t_orderdetail t where 1 = 1
 and orderID = #{orderID}
 and productID = #{productID}
 and price = #{price}
 and number = #{number}
 and productName = #{productName}
 and fee = #{fee}
 and total0 = #{total0}
 and isComment = #{isComment}
 and lowStocks = #{lowStocks}
 and score = #{score}
 and specId = #{specId}
 and specInfo = #{specInfo}
 and giftID = #{giftID}

order by id
limit #{offset}, #{pageSize}

select t.*,(select p.picture from t_product p where p.id=t.productID) picture 
  from t_orderdetail t where 1=1
 and orderID = #{orderID}
 and productID = #{productID}
 and price = #{price}
 and number = #{number}
 and productName = #{productName}
 and fee = #{fee}
 and total0 = #{total0}
 and isComment = #{isComment}
 and lowStocks = #{lowStocks}
 and score = #{score}
 and specId = #{specId}
 and specInfo = #{specInfo}
 and giftID = #{giftID}

order by id

select count(1) from t_orderdetail t where 1 = 1
 and orderID = #{orderID}
 and productID = #{productID}
 and price = #{price}
 and number = #{number}
 and productName = #{productName}
 and fee = #{fee}
 and total0 = #{total0}
 and isComment = #{isComment}
 and lowStocks = #{lowStocks}
 and score = #{score}
 and specId = #{specId}
 and specInfo = #{specInfo}
 and giftID = #{giftID}
