package com.tuisitang.modules.shop.entity;import java.io.Serializable;import com.fasterxml.jackson.annotation.JsonIgnore;import com.tuisitang.common.cache.memcached.MemcachedObjectType;import com.tuisitang.common.cache.memcached.SpyMemcachedClient;import net.jeeshop.core.dao.page.PagerModel;/**     * @{#} AccountRank.java    *     * 会员等级 Entity   * * <p>Copyright: Copyright(c) 2013 </p>  * <p>Company: TST</p> * @version 1.0 * @author <a href="mailto:paninxb@gmail.com">panin</a>    */public class AccountRank extends PagerModel<AccountRank> implements Serializable {	/**	 * 	 */	private static final long serialVersionUID = 1L;	protected Long id;	private String code;	private String name;	private double discount;	private int minScore;	private int maxScore;	private String remark;		public static final String RANK_CODE_R1 = "R1";	public static final String RANK_CODE_R2 = "R2";	public static final String RANK_CODE_R3 = "R3";	public static final String RANK_CODE_R4 = "R4";		public void clear() {		super.clear();		id = null;		code = null;		name = null;		discount = 1.00;		minScore = 0;		maxScore = 0;		remark = null;	}	public Long getId() {		return id;	}	public void setId(Long id) {		this.id = id;	}	public String getCode() {		return code;	}	public void setCode(String code) {		this.code = code;	}	public String getName() {		return name;	}	public void setName(String name) {		this.name = name;	}		public double getDiscount() {		return discount;	}	public void setDiscount(double discount) {		this.discount = discount;	}	public int getMinScore() {		return minScore;	}	public void setMinScore(int minScore) {		this.minScore = minScore;	}	public int getMaxScore() {		return maxScore;	}	public void setMaxScore(int maxScore) {		this.maxScore = maxScore;	}	public String getRemark() {		return remark;	}	public void setRemark(String remark) {		this.remark = remark;	}		@JsonIgnore	public static String getKey() {		return MemcachedObjectType.AccountRank.getPrefix() + "all";	}	@JsonIgnore	public static String getKey(Long id) {		return MemcachedObjectType.AccountRank.getPrefix() + "id" + SpyMemcachedClient.SEP + id;	}	@JsonIgnore	public static String getKey(String code) {		return MemcachedObjectType.AccountRank.getPrefix() + "code" + SpyMemcachedClient.SEP + code;	}}