package com.tuisitang.modules.shop.entity;import java.io.Serializable;import net.jeeshop.core.dao.QueryModel;import com.fasterxml.jackson.annotation.JsonIgnore;import com.tuisitang.common.cache.memcached.MemcachedObjectType;import com.tuisitang.common.cache.memcached.SpyMemcachedClient;import com.tuisitang.common.utils.ValidateUtils;/**     * @{#} Address.java    *     * 会员地址 Entity   * * <p>Copyright: Copyright(c) 2013 </p>  * <p>Company: TST</p> * @version 1.0 * @author <a href="mailto:paninxb@gmail.com">panin</a>    */public class Address extends QueryModel<Address> implements Serializable {	private static final long serialVersionUID = 1L;	protected Long id;	private Long accountId;	private String name;	private Long provinceId;	private String provinceCode;	private String provinceName;	private Long cityId;	private String cityCode;	private String cityName;	private Long countyId;	private String countyCode;	private String countyName;	private String address;	private String mobile;	private String phone;	private String zip;	private String isDefault;	public static final String DEFAULT_ADDRESS_Y = "y";	public static final String DEFAULT_ADDRESS_N = "n";	public Long getId() {		return id;	}	public void setId(Long id) {		this.id = id;	}	public Long getAccountId() {		return accountId;	}	public void setAccountId(Long accountId) {		this.accountId = accountId;	}	public String getName() {		return name;	}	public void setName(String name) {		this.name = name;	}	public Long getProvinceId() {		return provinceId;	}	public void setProvinceId(Long provinceId) {		this.provinceId = provinceId;	}	public String getProvinceCode() {		return provinceCode;	}	public void setProvinceCode(String provinceCode) {		this.provinceCode = provinceCode;	}	public String getProvinceName() {		return provinceName;	}	public void setProvinceName(String provinceName) {		this.provinceName = provinceName;	}	public Long getCityId() {		return cityId;	}	public void setCityId(Long cityId) {		this.cityId = cityId;	}	public String getCityCode() {		return cityCode;	}	public void setCityCode(String cityCode) {		this.cityCode = cityCode;	}	public String getCityName() {		return cityName;	}	public void setCityName(String cityName) {		this.cityName = cityName;	}	public Long getCountyId() {		return countyId;	}	public void setCountyId(Long countyId) {		this.countyId = countyId;	}	public String getCountyCode() {		return countyCode;	}	public void setCountyCode(String countyCode) {		this.countyCode = countyCode;	}	public String getCountyName() {		return countyName;	}	public void setCountyName(String countyName) {		this.countyName = countyName;	}	public String getAddress() {		return address;	}	public void setAddress(String address) {		this.address = address;	}	public String getMobile() {		return mobile;	}	public void setMobile(String mobile) {		this.mobile = mobile;	}	public String getPhone() {		return phone;	}	public void setPhone(String phone) {		this.phone = phone;	}	public String getZip() {		return zip;	}	public void setZip(String zip) {		this.zip = zip;	}	public String getIsDefault() {		return isDefault;	}	public void setIsDefault(String isDefault) {		this.isDefault = isDefault;	}	@JsonIgnore	public String getMaskMobile() {		if (ValidateUtils.isMobile(mobile)) {			return mobile.replaceAll("(\\d{3})\\d{4}(\\d{4})", "$1****$2");		} else {			return mobile;		}	}	@JsonIgnore	public static String getKeyById(Long id) {		return MemcachedObjectType.Address.getPrefix() + "id" + SpyMemcachedClient.SEP + id;	}	@JsonIgnore	public static String getKeyByAccountId(Long accountId) {		return MemcachedObjectType.Address.getPrefix() + "accountId" + SpyMemcachedClient.SEP + accountId;	}}