package com.tuisitang.modules.shop.entity;import java.io.Serializable;import com.tuisitang.common.cache.memcached.MemcachedObjectType;import com.tuisitang.common.cache.memcached.SpyMemcachedClient;import net.jeeshop.core.dao.page.PagerModel;/**     * @{#} Express.java   *  * Express Entity *  * <p>Copyright: Copyright(c) 2013 </p>  * <p>Company: TST</p> * @version 1.0 * @author <a href="mailto:paninxb@gmail.com">panin</a>    */public class Express extends PagerModel<Express> implements Serializable {	private static final long serialVersionUID = 1L;	protected Long id;	private String code;	private String name;	private double fee;	private int sort;	public void clear() {		super.clear();		id = null;		code = null;		name = null;		fee = 0;		sort = 0;	}	public Long getId() {		return id;	}	public void setId(Long id) {		this.id = id;	}	public String getCode() {		return code;	}	public void setCode(String code) {		this.code = code;	}	public String getName() {		return name;	}	public void setName(String name) {		this.name = name;	}	public double getFee() {		return fee;	}	public void setFee(double fee) {		this.fee = fee;	}	public int getSort() {		return sort;	}	public void setSort(int sort) {		this.sort = sort;	}	public static String getKey() {		return MemcachedObjectType.Express.getPrefix() + "all";	}	public static String getKey(Long id) {		return MemcachedObjectType.Express.getPrefix() + "id" + SpyMemcachedClient.SEP + id;	}	public static String getKey(String code) {		return MemcachedObjectType.Express.getPrefix() + "code" + SpyMemcachedClient.SEP + code;	}}