package com.tuisitang.modules.shop.entity;import java.io.Serializable;import net.jeeshop.core.dao.page.PagerModel;/**     * @{#} Notice.java    *     * Notice Entity   * * <p>Copyright: Copyright(c) 2013 </p>  * <p>Company: TST</p> * @version 1.0 * @author <a href="mailto:paninxb@gmail.com">panin</a>    */@Deprecatedpublic class Notice extends PagerModel<Notice> implements Serializable {	private static final long serialVersionUID = 1L;	protected Long id;	private String title;	private String content;	private String createtime;	private int top;	public Notice() {	}	public Notice(int top) {		this.top = top;	}	public void clear() {		super.clear();		id = null;		title = null;		content = null;		createtime = null;	}	public Long getId() {		return id;	}	public void setId(Long id) {		this.id = id;	}	public String getTitle() {		return title;	}	public void setTitle(String title) {		this.title = title;	}	public String getContent() {		return content;	}	public void setContent(String content) {		this.content = content;	}	public String getCreatetime() {		return createtime;	}	public void setCreatetime(String createtime) {		this.createtime = createtime;	}	public int getTop() {		return top;	}	public void setTop(int top) {		this.top = top;	}}