package com.tuisitang.modules.shop.entity;import java.io.Serializable;import net.jeeshop.core.dao.page.PagerModel;/**     * @{#} Reply.java *  * <p>Copyright: Copyright(c) 2013 </p>  * <p>Company: TST</p> * @version 1.0 * @author <a href="mailto:paninxb@gmail.com">panin</a>    */public class Reply extends PagerModel<Reply> implements Serializable {		private static final long serialVersionUID = 1L;		protected Long id;	private int commentID;	private String content;	private String createdate;	public void clear() {		super.clear();		id = null;		commentID = 0;		content = null;		createdate = null;	}	public Long getId() {		return id;	}	public void setId(Long id) {		this.id = id;	}	public int getCommentID() {		return commentID;	}	public void setCommentID(int commentID) {		this.commentID = commentID;	}	public String getContent() {		return content;	}	public void setContent(String content) {		this.content = content;	}	public String getCreatedate() {		return createdate;	}	public void setCreatedate(String createdate) {		this.createdate = createdate;	}}