package com.tuisitang.modules.shop.entity;import java.io.Serializable;import net.jeeshop.core.dao.page.PagerModel;/**     * @{#} Task.java *  * <p>Copyright: Copyright(c) 2013 </p>  * <p>Company: TST</p> * @version 1.0 * @author <a href="mailto:paninxb@gmail.com">panin</a>    */public class Task extends PagerModel<Task> implements Serializable {	private static final long serialVersionUID = 1L;	protected Long id;	private String code;	private String name;	private String sleep;	private String unit;	private String nextWorkTime;	private String currentStatus;	private String clz;	private Thread thread;	public static final String task_code_ManageIndexReportTask = "ManageIndexReportTask";	public static final String task_currentStatus_wait = "wait";	public static final String task_currentStatus_run = "run";	public static final String task_currentStatus_stop = "stop";	public void clear() {		super.clear();		id = null;		code = null;		name = null;		sleep = null;		unit = null;		nextWorkTime = null;		currentStatus = null;		clz = null;	}	public Long getId() {		return id;	}	public void setId(Long id) {		this.id = id;	}	public String getCode() {		return code;	}	public void setCode(String code) {		this.code = code;	}	public String getName() {		return name;	}	public void setName(String name) {		this.name = name;	}	public String getSleep() {		return sleep;	}	public void setSleep(String sleep) {		this.sleep = sleep;	}	public String getUnit() {		return unit;	}	public void setUnit(String unit) {		this.unit = unit;	}	public String getNextWorkTime() {		return nextWorkTime;	}	public void setNextWorkTime(String nextWorkTime) {		this.nextWorkTime = nextWorkTime;	}	public String getCurrentStatus() {		return currentStatus;	}	public void setCurrentStatus(String currentStatus) {		this.currentStatus = currentStatus;	}	public String getClz() {		return clz;	}	public void setClz(String clz) {		this.clz = clz;	}	public Thread getThread() {		return thread;	}	public void setThread(Thread thread) {		this.thread = thread;	}}