package net.jeeshop.services.front.attribute_link.dao.impl;import java.util.List;import net.jeeshop.core.dao.BaseDao;import net.jeeshop.core.dao.page.PagerModel;import com.tuisitang.modules.shop.entity.Attribute;import net.jeeshop.services.front.attribute_link.bean.AttributeLink;import net.jeeshop.services.front.attribute_link.dao.Attribute_linkDao;public class Attribute_linkDaoImpl implements Attribute_linkDao {	private BaseDao dao;	public void setDao(BaseDao dao) {		this.dao = dao;	}	public PagerModel selectPageList(AttributeLink e) {		return dao.selectPageList("front.attribute_link.selectPageList",				"front.attribute_link.selectPageCount", e);	}	public List selectList(AttributeLink e) {		return dao.selectList("front.attribute_link.selectList", e);	}	public AttributeLink selectOne(AttributeLink e) {		return (AttributeLink) dao.selectOne("front.attribute_link.selectOne", e);	}	public int delete(AttributeLink e) {		return dao.delete("front.attribute_link.delete", e);	}	public int update(AttributeLink e) {		return dao.update("front.attribute_link.update", e);	}	public int deletes(Long[] ids) {		AttributeLink e = new AttributeLink();		for (int i = 0; i < ids.length; i++) {			e.setId(ids[i]);			delete(e);		}		return 0;	}	public int insert(AttributeLink e) {		return dao.insert("front.attribute_link.insert", e);	}	public int deleteById(long id) {		return dao.delete("front.attribute_link.deleteById", id);	}	public int deleteByCondition(AttributeLink e) {		return dao.delete("front.attribute_link.deleteByCondition", e);	}		public AttributeLink selectById(Long id) {		return (AttributeLink) dao.selectOne("account.selectById", id);	}}