package net.jeeshop.services.front.questionnaire.impl;import net.jeeshop.core.ServersManager;import net.jeeshop.services.front.questionnaire.QuestionnaireService;import com.tuisitang.modules.shop.entity.Questionnaire;import net.jeeshop.services.front.questionnaire.dao.QuestionnaireDao;public class QuestionnaireServiceImpl extends ServersManager<Questionnaire>		implements QuestionnaireService {	private QuestionnaireDao questionnaireDao;	public void setQuestionnaireDao(QuestionnaireDao questionnaireDao) {		this.questionnaireDao = questionnaireDao;	}		/**	 * 新增问卷	 */	@Override	public void insert(Questionnaire e) {		if(e==null){			throw new NullPointerException("e is null");		}						super.insert(e);	}	/**	 * 修改问卷	 */	@Override	public void update(Questionnaire e) {				super.update(e);	}}