package net.jeeshop.services.manage.account.impl;import net.jeeshop.core.ServersManager;import net.jeeshop.services.manage.account.AccountService;import com.tuisitang.modules.shop.entity.Account;import net.jeeshop.services.manage.account.dao.AccountDao;import org.apache.commons.lang3.StringUtils;public class AccountServiceImpl extends ServersManager<Account> implements		AccountService {	private AccountDao accountDao;	public void setAccountDao(AccountDao accountDao) {		this.accountDao = accountDao;	}		/**	 * 批量删除	 * 	 * @param ids	 * @return	 */	public int deletes(Long[] ids) {		if (ids == null || ids.length == 0) {			throw new NullPointerException("id不能全为空！");		}				for (int i = 0; i < ids.length; i++) {			if(ids[i] == null){				throw new NullPointerException("id不能为空！");			}			accountDao.deleteById(ids[i]);		}		return 0;	}	}