package net.jeeshop.services.manage.bookingorder.dao.impl;import java.util.List;import net.jeeshop.core.dao.BaseDao;import net.jeeshop.core.dao.page.PagerModel;import net.jeeshop.services.manage.bookingorder.dao.BookingOrderDao;import com.tuisitang.modules.shop.entity.BookingOrder;/**     * @{#} BookingOrderDaoImpl.java   *  * 预订单/预约单 Dao *  * <p>Copyright: Copyright(c) 2013 </p>  * <p>Company: TST</p> * @version 1.0 * @author <a href="mailto:paninxb@gmail.com">panin</a>    */public class BookingOrderDaoImpl implements BookingOrderDao {	private BaseDao dao;	public void setDao(BaseDao dao) {		this.dao = dao;	}	public int insert(BookingOrder e) {		return dao.insert("manage.bookingorder.insert", e);	}	public int delete(BookingOrder e) {		return dao.delete("manage.bookingorder.delete", e);	}	public int update(BookingOrder e) {		return dao.update("manage.bookingorder.update", e);	}	public BookingOrder selectOne(BookingOrder e) {		return (BookingOrder) dao.selectOne("manage.bookingorder.selectOne", e);	}	public PagerModel<BookingOrder> selectPageList(BookingOrder e) {		return dao.selectPageList("manage.bookingorder.selectPageList", "manage.bookingorder.selectPageCount", e);	}	public List<BookingOrder> selectList(BookingOrder e) {		return dao.selectList("manage.bookingorder.selectList", e);	}	public int deleteById(long id) {		return dao.delete("manage.bookingorder.deleteById", id);	}	@Override	public BookingOrder selectById(Long id) {		return (BookingOrder) dao.selectOne("manage.bookingorder.selectById",				id);	}}