package net.jeeshop.services.manage.orderdetailExpress.dao.impl;import java.util.List;import net.jeeshop.core.dao.BaseDao;import net.jeeshop.core.dao.page.PagerModel;import net.jeeshop.services.manage.orderdetailExpress.dao.OrderdetailExpressDao;import com.tuisitang.modules.shop.entity.OrderdetailExpress;public class OrderdetailExpressDaoImpl implements OrderdetailExpressDao {	private BaseDao dao;	public void setDao(BaseDao dao) {		this.dao = dao;	}	/**	 * 添加	 * 	 * @param e	 * @return	 */	public int insert(OrderdetailExpress e) {		return dao.insert("manage.orderdetailexpress.insert", e);	}	/**	 * 删除	 * 	 * @param e	 * @return	 */	public int delete(OrderdetailExpress e) {		return dao.delete("manage.orderdetailexpress.delete", e);	}	/**	 * 修改	 * 	 * @param e	 * @return	 */	public int update(OrderdetailExpress e) {		return dao.update("manage.orderdetailexpress.update", e);	}	/**	 * 查询一条记录	 * 	 * @param e	 * @return	 */	public OrderdetailExpress selectOne(OrderdetailExpress e) {		return (OrderdetailExpress) dao.selectOne(				"manage.orderdetailexpress.selectOne", e);	}	/**	 * 分页查询	 * 	 * @param e	 * @return	 */	public PagerModel<OrderdetailExpress> selectPageList(OrderdetailExpress e) {		return dao.selectPageList("manage.orderdetailexpress.selectPageList",				"manage.orderdetailexpress.selectPageCount", e);	}	/**	 * 根据条件查询所有	 * 	 * @return	 */	public List<OrderdetailExpress> selectList(OrderdetailExpress e) {		return dao.selectList("manage.orderdetailexpress.selectList", e);	}	/**	 * 根据ID来删除一条记录	 * 	 * @param id	 */	public int deleteById(long id) {		return dao.delete("manage.orderdetailexpress.deleteById", id);	}	/**	 * 根据ID查询一条记录	 * 	 * @param id	 * @return	 */	public OrderdetailExpress selectById(Long id) {		return (OrderdetailExpress) dao.selectOne(				"manage.orderdetailexpress.selectById", id);	}}