package net.jeeshop.services.manage.questionnaire.impl;import net.jeeshop.core.ServersManager;import net.jeeshop.services.manage.questionnaire.QuestionnaireService;import com.tuisitang.modules.shop.entity.Questionnaire;import net.jeeshop.services.manage.questionnaire.dao.QuestionnaireDao;import org.apache.commons.lang3.StringUtils;public class QuestionnaireServiceImpl extends ServersManager<Questionnaire>		implements QuestionnaireService {	private QuestionnaireDao questionnaireDao;	public void setQuestionnaireDao(QuestionnaireDao questionnaireDao) {		this.questionnaireDao = questionnaireDao;	}		/**	 * 新增问卷	 */	@Override	public void insert(Questionnaire e) {		if(e==null){			throw new NullPointerException("e is null");		}						super.insert(e);	}	/**	 * 修改问卷	 */	@Override	public void update(Questionnaire e) {				super.update(e);	}	@Override	public void changeStatus(Long[] ids, String questionnaireStatusY) {		if(ids==null || ids.length==0){			return;		}				for(int i=0;i<ids.length;i++){			if(ids[i] == null){				throw new NullPointerException("id不能为空！");			}						Questionnaire e = new Questionnaire();			e.setId(ids[i]);			e.setStatus(questionnaireStatusY);			questionnaireDao.update(e);		}			}}