package net.jeeshop.services.manage.systemlog.impl;import net.jeeshop.core.ServersManager;import net.jeeshop.services.manage.systemlog.SystemlogService;import com.tuisitang.modules.shop.entity.Systemlog;import net.jeeshop.services.manage.systemlog.dao.SystemlogDao;public class SystemlogServiceImpl extends ServersManager<Systemlog> implements		SystemlogService {	private SystemlogDao systemlogDao;	public void setSystemlogDao(SystemlogDao systemlogDao) {		this.systemlogDao = systemlogDao;	}	@Override	public Systemlog selectFirstOne(String account) {		return systemlogDao.selectFirstOne(account);	}}