DROP TABLE IF EXISTS  `t_account`;
CREATE TABLE `t_account` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '编号',
  `mobile` varchar(20) COLLATE utf8_unicode_ci NOT NULL COMMENT '手机号',
  `nickname` varchar(45) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '昵称',
  `account` varchar(45) COLLATE utf8_unicode_ci NOT NULL COMMENT '用户名\n_out_当前时间戳',
  `password` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '密码',
  `accountType` varchar(25) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '会员类型。\nqq,sinawb,alipay',
  `trueName` varchar(45) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '真实姓名',
  `sex` varchar(2) COLLATE utf8_unicode_ci DEFAULT 's' COMMENT '性别。m:男,f：女,s:保密',
  `birthday` date DEFAULT NULL COMMENT '生日',
  `province` varchar(45) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '所在省份',
  `city` varchar(45) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '所在城市',
  `address` varchar(45) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '联系地址',
  `postcode` varchar(45) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '邮编',
  `cardType` varchar(45) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '证件类型',
  `cardNO` varchar(45) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '证件号码',
  `grade` int(11) DEFAULT '1' COMMENT '等级',
  `amount` varchar(45) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '消费额',
  `tel` varchar(45) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '电话',
  `email` varchar(45) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '邮箱',
  `emailIsActive` varchar(2) COLLATE utf8_unicode_ci DEFAULT 'n' COMMENT '邮箱是否已激活。y:已激活,n:未激活。默认n',
  `freeze` varchar(2) COLLATE utf8_unicode_ci NOT NULL DEFAULT 'n' COMMENT '是否冻结 n：未冻结，y：已冻结；默认n',
  `freezeStartdate` date DEFAULT NULL COMMENT '冻结开始时间',
  `freezeEnddate` date DEFAULT NULL COMMENT '冻结结束时间',
  `lastLoginTime` datetime DEFAULT NULL COMMENT '最后登陆时间',
  `lastLoginIp` varchar(45) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '最后登陆IP',
  `lastLoginArea` varchar(25) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '最后登陆地区',
  `diffAreaLogin` char(1) COLLATE utf8_unicode_ci DEFAULT 'n' COMMENT '是否是异地登陆y:是,n:否',
  `regeistDate` datetime DEFAULT NULL COMMENT '注册时间',
  `openId` varchar(45) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT 'QQ登陆返回',
  `accessToken` varchar(45) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT 'QQ登陆返回',
  `alipayUseId` varchar(45) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '支付宝快捷登陆返回的用户ID',
  `sinaWeiboID` varchar(45) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '新浪微博登陆返回的用户ID',
  `rank` varchar(5) COLLATE utf8_unicode_ci DEFAULT 'R1' COMMENT '会员等级。和t_accountType.code进行挂钩。默认R1',
  `score` int(11) DEFAULT '0' COMMENT '会员积分。默认0',
  `picture` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '会员头像',
  `smallPicture` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '小头像',
  `authStatus` tinyint(1) DEFAULT NULL COMMENT '认证状态：\n0 未认证\n1 已经实名认证\n2 已经企业认证\n3 个人和企业认证成功\n4 个人实名认证失败\n5 企业认证失败\n6 个人实名认证成功，企业实名认证失败\n7 企业实名认证成功，个人实名认证失败\n8 个人企业都认证失败',
  `inviteCode` varchar(64) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '邀请码',
  `inviteId` bigint(20) DEFAULT NULL COMMENT '邀请我的会员编号',
  `isMaster` varchar(2) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '是否是大师',
  `profit` decimal(10,0) DEFAULT NULL COMMENT '购买提成收益',
  `twoDimensionUrl` varchar(245) COLLATE utf8_unicode_ci DEFAULT NULL,
  PRIMARY KEY (`id`),
  UNIQUE KEY `account_UNIQUE` (`account`),
  UNIQUE KEY `alipayUseId_UNIQUE` (`alipayUseId`),
  UNIQUE KEY `openId_UNIQUE` (`openId`),
  UNIQUE KEY `email_UNIQUE` (`email`),
  UNIQUE KEY `sinaWeiboID_UNIQUE` (`sinaWeiboID`)
) ENGINE=InnoDB AUTO_INCREMENT=91 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci COMMENT='账户';

