DROP TABLE IF EXISTS  `t_order`;
CREATE TABLE `t_order` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `no` varchar(20) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '流水编号',
  `accountId` int(11) DEFAULT NULL COMMENT '会员编号',
  `rebate` decimal(10,2) DEFAULT NULL COMMENT '折扣',
  `createdate` datetime DEFAULT NULL COMMENT '创建日期',
  `status` varchar(10) COLLATE utf8_unicode_ci DEFAULT 'init' COMMENT 'nit:未审核；pass:已审核；\nsend：已发货；sign：已签收；cancel:已取消；file:已归档；\nfinish：交易完成；\n',
  `refundStatus` varchar(45) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '退款状态(直接借用了支付宝的退款状态)。\nWAIT_SELLER_AGREE：等待卖家同意退款；\nWAIT_BUYER_RETURN_GOODS：卖家同意退款，等待买家退货；\nWAIT_SELLER_CONFIRM_GOODS：买家已退货，等待卖家收到退货；\nREFUND_SUCCESS：卖家收到退货，退款成功，交易关闭\n',
  `amount` decimal(20,2) DEFAULT '0.00' COMMENT '订单总金额',
  `fee` decimal(20,2) DEFAULT '0.00' COMMENT '运费总金额',
  `ptotal` decimal(20,2) DEFAULT NULL COMMENT '商品总金额',
  `quantity` int(11) DEFAULT NULL COMMENT '商品总数量',
  `paystatus` varchar(2) COLLATE utf8_unicode_ci DEFAULT 'n' COMMENT 'n:未支付；\np:部分支付；\ny:全部支付\n',
  `updateAmount` varchar(1) COLLATE utf8_unicode_ci DEFAULT 'n' COMMENT 'n:没有修改过；y:修改过；默认:n',
  `expressCode` varchar(45) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '配送方式编码',
  `expressName` varchar(45) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '配送方式名称',
  `payCode` varchar(30) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '支付方式：alipay',
  `payName` varchar(30) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '支付方式：支付宝',
  `deliveryDayCode` varchar(30) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '送货日期',
  `deliveryDayName` varchar(30) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '送货日期名称',
  `otherRequirement` varchar(50) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '客户的附加要求',
  `remark` varchar(545) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '备注、支付宝的WIDsubject',
  `expressNo` varchar(45) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '快递运单号',
  `expressCompanyName` varchar(45) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '快递公司名称',
  `lowStocks` varchar(1) COLLATE utf8_unicode_ci DEFAULT 'n' COMMENT 'n:库存不足；y:库存充足。默认n',
  `confirmSendProductRemark` varchar(100) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '确认发货备注',
  `closedComment` varchar(1) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '此订单的所有订单项对应的商品都进行了评论，则此值为y，表示此订单的评论功能已经关闭，默认为null，在订单状态为已发货后，则用户可以对订单进行评价。',
  `score` int(11) DEFAULT NULL COMMENT '订单获赠的积分',
  PRIMARY KEY (`id`),
  KEY `order_status` (`status`)
) ENGINE=InnoDB AUTO_INCREMENT=10302 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci COMMENT='订单表';

DROP TABLE IF EXISTS  `t_orderdetail`;
