DROP TABLE IF EXISTS  `t_orderlog`;
CREATE TABLE `t_orderlog` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `orderid` varchar(11) COLLATE utf8_unicode_ci NOT NULL COMMENT '订单ID',
