DROP TABLE IF EXISTS  `t_product_catalog_rela`;
CREATE TABLE `t_product_catalog_rela` (
  `productId` int(11) DEFAULT NULL,
  `catalogId` int(11) DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

DROP TABLE IF EXISTS  `t_product_hit`;
CREATE TABLE `t_product_hit` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '编号',
  `productId` bigint(20) NOT NULL COMMENT '商品编号',
  `hit` int(11) NOT NULL DEFAULT '0' COMMENT '点击数',
  PRIMARY KEY (`id`,`productId`,`hit`),
  UNIQUE KEY `IDX_PRODUCT_ID` (`productId`)
) ENGINE=InnoDB AUTO_INCREMENT=78 DEFAULT CHARSET=utf8;

DROP TABLE IF EXISTS  `t_product_hit_trace`;
CREATE TABLE `t_product_hit_trace` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '编号',
  `product_id` bigint(20) NOT NULL COMMENT '产品编号',
  `product_name` varchar(100) NOT NULL COMMENT '产品名称',
  `session_id` varchar(64) NOT NULL COMMENT 'Session编号',
  `account_id` bigint(20) DEFAULT NULL COMMENT '会员编号',
  `hit_time` datetime NOT NULL COMMENT '点击时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=348 DEFAULT CHARSET=utf8 COMMENT='产品点击跟踪记录';

DROP TABLE IF EXISTS  `t_product_sellcount`;
CREATE TABLE `t_product_sellcount` (
  `productId` int(11) DEFAULT NULL,
  `sellCount` int(11) DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

DROP TABLE IF EXISTS  `t_product_stock`;
CREATE TABLE `t_product_stock` (
  `productId` int(11) DEFAULT NULL,
  `stock` int(11) DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

DROP TABLE IF EXISTS  `t_profit_log`;
CREATE TABLE `t_profit_log` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'id\n',
  `inviteId` int(11) NOT NULL COMMENT '邀请人账户Id',
  `inviteName` varchar(32) DEFAULT NULL COMMENT '邀请人姓名',
  `inviteeId` int(11) NOT NULL COMMENT '被邀请人账户Id',
  `inviteeName` varchar(32) DEFAULT NULL COMMENT '被邀请人姓名',
  `createDate` datetime DEFAULT NULL COMMENT '创建时间',
  `description` varchar(100) DEFAULT NULL COMMENT '收益描述',
  `profit` decimal(10,0) DEFAULT NULL COMMENT '收益',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=3 DEFAULT CHARSET=utf8 COMMENT='收益信息表';

DROP TABLE IF EXISTS  `t_questionnaire`;
CREATE TABLE `t_questionnaire` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(45) COLLATE utf8_unicode_ci NOT NULL COMMENT '问卷名称',
  `subtitle` varchar(45) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '副标题',
  `createdate` datetime DEFAULT NULL COMMENT '创建时间',
  `updatedate` datetime DEFAULT NULL COMMENT '最后修改时间',
  `createAccount` varchar(45) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '创建人',
  `updateAccount` varchar(45) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '最后修改人',
  `status` varchar(2) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '是否启用',
  `showStartDate` date DEFAULT NULL COMMENT '显示的开始日期',
  `showEndDate` date DEFAULT NULL COMMENT '显示的结束日期',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=6 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci COMMENT='问卷调查表';

DROP TABLE IF EXISTS  `t_questionnaireitem`;
CREATE TABLE `t_questionnaireitem` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `qid` varchar(45) COLLATE utf8_unicode_ci NOT NULL COMMENT '问卷id',
  `subject` varchar(45) COLLATE utf8_unicode_ci NOT NULL COMMENT '题目',
  `option1` varchar(45) COLLATE utf8_unicode_ci NOT NULL COMMENT '题目的内容',
  `type` varchar(45) COLLATE utf8_unicode_ci NOT NULL COMMENT '单选/多选/填空',
  `sort` int(11) DEFAULT '0' COMMENT '题目的顺序',
  `display` varchar(15) COLLATE utf8_unicode_ci DEFAULT 'lines' COMMENT '显示模式',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=141 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci COMMENT='问卷调查题目表';

DROP TABLE IF EXISTS  `t_questionnaireresult`;
CREATE TABLE `t_questionnaireresult` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `qid` varchar(45) COLLATE utf8_unicode_ci NOT NULL COMMENT '问卷ID',
  `accountId` int(11) DEFAULT NULL COMMENT '会员编号',
  `account` varchar(45) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '会员账号',
  `qItemID` varchar(45) COLLATE utf8_unicode_ci NOT NULL COMMENT '题目选项ID',
  `text` varchar(145) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '题目选项的内容',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci COMMENT='问卷调查结果表';

DROP TABLE IF EXISTS  `t_region`;
CREATE TABLE `t_region` (
  `id` int(11) NOT NULL COMMENT '编号',
  `code` varchar(45) NOT NULL COMMENT '省市区编码',
  `pcode` varchar(45) NOT NULL COMMENT '父编码',
  `name` varchar(45) NOT NULL COMMENT '省市区名称',
  `description` varchar(245) DEFAULT NULL COMMENT '描述',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='配送区域表';

DROP TABLE IF EXISTS  `t_reply`;
CREATE TABLE `t_reply` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `commentID` int(11) NOT NULL COMMENT '评论ID',
  `content` varchar(140) NOT NULL COMMENT '回复内容',
  `createdate` datetime DEFAULT NULL COMMENT '回复时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='评论回复表';

DROP TABLE IF EXISTS  `t_role`;
CREATE TABLE `t_role` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `role_name` varchar(45) NOT NULL COMMENT '角色名称',
