DROP PROCEDURE IF EXISTS `proc_update_picture`;
DELIMITER $$
CREATE PROCEDURE `proc_update_picture`()
begin
    DECLARE pid INT;
    DECLARE _done INT DEFAULT 0; 
    DECLARE i INT DEFAULT 0; 
    
    DECLARE cur_style10 CURSOR FOR SELECT id from t_product;   
    DECLARE CONTINUE HANDLER FOR NOT FOUND SET _done = 1;

    OPEN cur_style10;
    FETCH cur_style10 INTO pid;
    while _done<>1 do   
       	update t_product set picture = CONCAT('/attached/image/D-',i,'.jpg'), images = CONCAT('/attached/image/D-',i,'.jpg') where id = pid;
        set i = i+1;
        FETCH cur_style10 INTO pid; 
    end while;
    close cur_style10;       

END
$$
DELIMITER ;

