mapper.update:
UPDATE t_cart SET id= #{id}
	,account_id = #{accountId} 
	,session_id = #{sessionId}  
	,product_id = #{productId}  
	,spec_id = #{specId}  
	,spec_name = #{specName}  
	,product_name = #{productName}  
	,product_image = #{productImage}  
	,quantity = #{quantity} 
	,market_price = #{marketPrice} 
	,now_price = #{nowPrice} 
	,discount_price = #{discountPrice} 
	,fee = #{fee} 
	,activity_id = #{activityId} 
	,activity_name = #{activityName} 
	,activity_status = #{activityStatus} 
	,activity_type = #{activityType} 
	,discount_type = #{discountType} 
	,discount = #{discount} 
	,required_integral = #{requiredIntegral} 
	,present_integral = #{presentIntegral} 
WHERE id = #{id}

mapper.select:
SELECT 
FROM t_cart t 
WHERE t.id = #{id}

SELECT
FROM t_cart t
WHERE 1 = 1 order by id limit #{offset}, #{pageSize}

SELECT 
FROM t_cart t 
WHERE t.id = #{id}

SELECT 
FROM t_cart t 
WHERE t.account_id = #{accountId} AND t.product_id = #{productId}
 and t.spec_id = #{specId}

SELECT 
FROM t_cart t 
WHERE t.session_id = #{sessionId} AND t.product_id = #{productId}
 and t.spec_id = #{specId}

SELECT 
FROM t_cart t 
WHERE t.session_id = #{sessionId}
