mapper.insert:
insert into t_order
(
id,no,createdate,status,paystatus

	,accountId


	,account


	,rebate


	,remark


	,amount


	,amountExchangeScore


	,ptotal


	,quantity


	,fee


	,expressCode


	,expressName


	,payCode


	,payName


	,deliveryDayCode


	,deliveryDayName


	,otherRequirement


	,closedComment


	,score


	,orderpayId

)
values
(
id,#{no},now(),#{status},#{paystatus}

	,#{accountId}


	,#{account}


	,#{rebate}


	,#{remark}


	,#{amount}


	,#{amountExchangeScore}


	,#{ptotal}


	,#{quantity}


	,#{fee}


	,#{expressCode}


	,#{expressName}


	,#{payCode}


	,#{payName}


	,#{deliveryDayCode}


	,#{deliveryDayName}


	,#{otherRequirement}


	,#{closedComment}


	,#{score}


    ,#{orderpayId}

)

mapper.update:
update t_order
set id=#{id}

	,status=#{status}


	,refundStatus=#{refundStatus}


	,paystatus=#{paystatus}


	,accountId=#{accountId}


	,account=#{account}


	,rebate=#{rebate}


	,remark=#{remark}


	,amount=#{amount}


	,ptotal=#{ptotal}


	,quantity=#{quantity}


	,expressCode=#{expressCode}


	,expressName=#{expressName}


	,payCode=#{payCode}


	,payName=#{payName}


	,payDate=#{payDate}


	,deliveryDayCode=#{deliveryDayCode}


	,deliveryDayName=#{deliveryDayName}


	,otherRequirement=#{otherRequirement}


	,deliveryDate=#{deliveryDate}


	,lowStocks=#{lowStocks}


	,closedComment=#{closedComment}


	,orderpayId=#{orderpayId}

where 1=1 and id=#{id}
